INSERT INTO `SERVMASK_PREFIX_options` VALUES ('246','options_registration_steps_step_1_text_block_2','הסיפור החברתי הנוכחי נועד לעזור לכם ולילדכם/תכם לצלוח את התהליך הראשוני של לקיחת סלנייטו שנרשם לילדך/תך ע\"י הרופא/ה, כלומר הכנסת התרופה לשגרת הערב ובליעתה. הסיפור הינו אישי, מותאם לרמת ההבנה ולגיל של הילד/ה ומותאם לשגרת הערב בביתכם.','no');
