INSERT INTO `SERVMASK_PREFIX_options` VALUES ('309','options_registration_and_login_registration_steps_step_1_text_block_1','סיפור חברתי הינו טכניקה מוכרת שנועדה לעזור לילדים להבין סיטואציות ביום יום ולקחת בהן חלק פעיל יותר. שימוש בסיפור חברתי באופן חזותי, מובנה וברור מהווה יתרון משמעותי להנעת תהליכים והובלת שינויים בקרב ילדים על הרצף האוטיסטי.','no');
