INSERT INTO `SERVMASK_PREFIX_options` VALUES ('323','options_registration_and_login_registration_steps_step_2_text_block_2','<strong>המינון המקובל בדרך כלל הוא: </strong>\r\n\r\nהמינון ההתחלתי המומלץ הוא 2 מ\"ג (שתי טבליות של 1 מ\"ג) פעם ביום. אם אין שיפור בתסמינים שלך/של ילדך, הרופא שלך עשוי להעלות את מינון הסלנייטו כדי למצוא את המינון המתאים ביותר עבורך/עבור ילדך.\r\n\r\nהמינון היומי המרבי שאתה/ילדך תקבלו הוא 10 מ\"ג (שתי טבליות של 5 מ\"ג).\r\n\r\nהמינון ואופן הטיפול יקבעו על ידי הרופא בלבד.','no');
